import React, { useEffect, useRef } from 'react';
import {
    MDBBtn,
    MDBModal,
    MDBModalDialog,
    MDBModalContent,
    MDBModalHeader,
    MDBModalTitle,
    MDBModalBody,
    MDBModalFooter
} from 'mdb-react-ui-kit';
// import '../../styles/setting.css'
import { API_URL } from '../../Helper';

const Register = (props) => {
    const [name, setName] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [phoneNo, setPhoneNo] = React.useState('');
    const [profile, setProfile] = React.useState([]);
    const [imgUrl, setImgUrl] = React.useState([]);
    const ref = useRef();

    const close = () => {
        props.close(false)
    }
    const uploadProfile = (e) => {
        setProfile(e.target.files)
        let newArr = imgUrl;
        for (let i = 0; i < e.target.files.length; i++) {

            newArr.unshift({ name: e.target.files[i].name, type: e.target.files[i].type, path: URL.createObjectURL(e.target.files[i]), new: true });
        }
        setImgUrl(newArr)
    }
    return (
        <>
            <MDBModal staticBackdrop open={props.open} tabIndex='-1' onClose={close} >
                <MDBModalDialog>
                    <MDBModalContent>


                        <MDBModalHeader>
                            <MDBModalTitle>Register</MDBModalTitle>
                            <MDBBtn className='btn-close' color='none' onClick={close} ></MDBBtn>
                        </MDBModalHeader>
                        <MDBInput className='mb-4' required onChange={(e) => setName(e.target.value)} value={name} label='Name' />
                        <MDBInput className='mb-4' required onChange={(e) => setPassword(e.target.value)} value={password} label='Password' />
                        <MDBInput className='mb-4' required onChange={(e) => setPhoneNo(e.target.value)} value={phoneNo} label='Phone No' />
                        <MDBInput className='mb-4' required onChange={(e) => setEmail(e.target.value)} value={email} label='Email' />
                        <MDBInput className='mb-4' required onChange={(e) => setPhoneNo(e.target.value)} value={phoneNo} label='Phone No' />
                        <label htmlFor="file-upload" className="custom-file-upload">
                                <i className="fa fa-cloud-upload"></i> <span>Upload Profile Image</span>
                            </label>
                            <input accept='image/*' ref={ref} id="file-upload" type="file"  onChange={(e)=>uploadProfile(e)} />
                        <MDBModalBody>



                        </MDBModalBody>

                        <MDBModalFooter>
                            <MDBBtn color='secondary' onClick={close}>
                                Close
                            </MDBBtn>
                            <MDBBtn 
                                onClick={""} >Save changes</MDBBtn>
                        </MDBModalFooter>
                    </MDBModalContent>
                </MDBModalDialog>
            </MDBModal>
        </>
    );
}

export default Register;
