import React from 'react';
import {
  MDBBtn
} from 'mdb-react-ui-kit';

import bg from '../../assets/bg.webp';

import Register from '../../components/modals/Register';

export default function Content() {
  const [registerModal, setRegisterModal] = React.useState(false);

  const registerOpenModal = () => {
    setRegisterModal(true);
}

const close =(value) =>{
  console.log(value)
  if(value) signUp(value)
  setRegisterModal(false)
}
const signUp = async (value) => {
  try {
      setLoading(true)
      let response = await post('api/user/create', value);
      if (response.status === 200) {
        console.log(response.data)
          // toast.success(response.data.message);
          // let newArr = productData;
          // newArr.unshift({ ...response.data.data });
          // setProductData(newArr);
          // setLoading(false);
      }
  }
  catch (error) {
      if (error.response && error.response.status === 400) {
          toast.error(error.response.data.error)
      }
      else {
          toast.error(error.message)
      }
      setLoading(false);
  }

}
  return (
    <header>
      <div
        id='main-bg'
        className='p-5 text-center bg-image'
        style={{ backgroundImage: `url(${bg})` }}
      >
        <div className='mask' style={{ backgroundColor: 'rgba(0, 0, 0, 0.7)' }}>
          <div className='d-flex justify-content-center align-items-center h-100'>
            <div className='text-white'>
              <h1 className='mb-3'>Welcome to our Jewellery Website</h1>
              <h5 className='mb-4'></h5>
              <MDBBtn
                className="m-2"
                tag="a"
                outline
                size="lg"
                rel="nofollow"
              >
                Login
              </MDBBtn>
              <MDBBtn
                className="m-2"
                tag="a"
                outline
                size="lg"
                onClick={registerOpenModal}
              >
               Register
              </MDBBtn>

            </div>
          </div>
        </div>
      </div>
      {registerModal && <Register open={registerModal} close={close} />}

    </header>
  );
}
