import React, { useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import Table from '../../components/Table';
import ProductImage from '../../components/modals/ProductImage';
import Detail from '../../components/modals/Detail';
import { get, post, patch, remove } from '../../Api';
import {
    MDBRow, MDBCol, MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBSpinner
} from 'mdb-react-ui-kit';
const Order = () => {
    const [loading, setLoading] = React.useState(true);
    const [productImageModal, setProductImageModal] = React.useState(false);
    const [productImage, setProductImage] = React.useState('');
    const [viewAddressModal, setViewAddressModal] = React.useState(false);
    const [viewAddressData, setViewAddressData] = React.useState('');
    const [orderData, setOrderData] = React.useState([]);
    const orderHeader = ['referenceNo', 'product', 'price', 'status', 'Date', 'action'];
    const getOrderData = async () => {
        try {
            setLoading(true)
            const response = await get('api/order/getAll/admin');
            if (response.status === 200) {
                setLoading(false);
                setOrderData(response.data.data);
                console.log(response.data.data)
            }
        }
        catch (error) {
            if (error.response && error.response.status === 400) {
                toast.error(error.response.data.error)
            }
            else {
                toast.error(error.message)
            }
            setLoading(false);
        }
    }
    useEffect(() => {
        getOrderData();
    }, []);

    const productView = (data) => {
        setProductImageModal(true);
        setProductImage(data)
    }
    const closeProductImageModal = async () => {
        setProductImageModal(false);
    }

    const viewAddress = (address) =>{
        console.log(address)
        setViewAddressModal(true);
        setViewAddressData(address)
    }
    const closeViewAddressModal = async () => {
        setViewAddressModal(false);
    }
    return (
        <MDBRow className='custom-margin-top'>
            <MDBCol md='12' >
                <MDBCard alignment='center' style={{ height: '100%' }}>
                    <MDBCardHeader>
                        <span className='text-primary'>Order</span> <span className='text-danger'>({orderData.length})</span>
                    </MDBCardHeader>
                    <MDBCardBody className='custom-height-setting'>
                        {(loading === false) ? (<Table title={'order'} header={orderHeader} data={orderData} productData={productView} viewAddress={viewAddress} />)

                            : (<MDBSpinner role='status'>
                                <span className='visually-hidden'>Loading...</span>
                            </MDBSpinner>)
                        }
                    </MDBCardBody>
                </MDBCard>
            </MDBCol>
            <ToastContainer />
            {productImageModal && <ProductImage open={productImageModal} closeModal={closeProductImageModal} data={productImage} />}
            {viewAddressModal && <Detail open={viewAddressModal} closeModal={closeViewAddressModal} data={viewAddressData} />}

        </MDBRow>

    )
}

export default Order
