import React, { useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import Table from '../../components/Table';
import { get, post, patch, remove } from '../../Api';

const Deal = () => {
    const [loading, setLoading] = React.useState(true);

    const [dealData, setDealData] = React.useState([]);
    const productHeader = ['product', 'price', 'status', 'dealDate', 'action'];
    const getOrderData = async () => {
        try {
            setLoading(true)
            const response = await get('api/order/getAll');
            if (response.status === 200) {
                setLoading(false);
                setDealData(response.data.data);
            }
        }
        catch (error) {
            if (error.response && error.response.status === 400) {
                toast.error(error.response.data.error)
            }
            else {
                toast.error(error.message)
            }
            setLoading(false);
        }
    }
    useEffect(() => {
        getOrderData();
    }, []);
    return (
        <MDBRow className='custom-margin-top'>
            <MDBCol md='3' >
                <MDBCard alignment='center' >
                    <MDBCardHeader>

                        <span className='text-primary'>Categories</span> <span className='text-danger'>({categoryData.length})</span>
                        <MDBBtn onClick={() => setCatModal(true)} size='sm' className='text-primary position-absolute top-0 end-0 mt-1 me-3' tag='a' color='light' floating>
                            <MDBTooltip tag='span' title="Add Category">
                                <MDBIcon fas icon="add" />
                            </MDBTooltip>
                        </MDBBtn>

                    </MDBCardHeader>
                    <MDBCardBody className='custom-height-setting'>
                        {(loading === false) ? (<Table title={'category'} header={categoryHeader} data={categoryData} editData={openCategoryModal} deleteData={deleteCategoryConfirm} />)
                            : (
                                <MDBSpinner role='status'>
                                    <span className='visually-hidden'>Loading...</span>
                                </MDBSpinner>)
                        }
                    </MDBCardBody>
                </MDBCard>
            </MDBCol>
            <MDBCol md='9' >
                <MDBCard alignment='center' style={{ height: '100%' }}>
                    <MDBCardHeader>

                        <span className='text-primary'>Products</span> <span className='text-danger'>({productData.length})</span>
                        {
                            categoryData.length > 0 && (<MDBBtn onClick={() => setProductModal(true)} size='sm' className='text-primary position-absolute top-0 end-0 mt-1 me-3' tag='a' color='light' floating>

                                <MDBTooltip tag='span' title="Add Product">
                                    <MDBIcon fas icon="add" />
                                </MDBTooltip>
                            </MDBBtn>)
                        }


                    </MDBCardHeader>
                    <MDBCardBody className='custom-height-setting'>
                        {(loading === false) ? (<Table title={'deal'} header={productHeader} data={productData} editData={openProductModal} deleteData={deleteProductConfirm} />)

                            : (<MDBSpinner role='status'>
                                <span className='visually-hidden'>Loading...</span>
                            </MDBSpinner>)
                        }
                    </MDBCardBody>
                </MDBCard>
            </MDBCol>


            <ToastContainer />


        </MDBRow>
    )
}

export default Deal
